const userSchema = new Schema({
    titulo:{
      type:String,
      required:true,
      unique:true
    },
    actividades:{
        type:[String],
        required:true
    },
    auditor:{
        type:String,
        default:""
    }
 },
 {
     timestamps:true,
     versionKey:false,
 }
);

export default model("Tasks",userSchema);

