        username,
        comentario,
        archivo,
        fechahora
    });

    const saveComment = await newComment.save();
    res.status(200).json({message:"comentario ingresado con exito"});
}

export const GetComments = async(req,res)=>{
    const getAll = await Chat.find();
    res.send(getAll);
}

